dt-api.example:
var table = $('#myTable').DataTable({
    buttons: [{
        extend: 'collection',
        text: 'print',
        buttons: ['csv', 'pdf']
    }]
});

table.button( 0 ).collectionRebuild(['csv', 'pdf', 'excel']);

var table = $('#myTable').DataTable({
    buttons: [{
        extend: 'collection',
        text: 'print',
        buttons: ['csv', 'pdf']
    }]
});

table.button( 0 ).collectionRebuild(['pdf']);

var table = $('#myTable').DataTable({
    buttons: [{
        extend: 'collection',
        text: 'print',
        buttons: ['csv', 'pdf']
    }]
});

table.button( 0 ).collectionRebuild([]);
