dt-api.example:
var table = $('#myTable').DataTable({
    buttons: [
		{
			extend: 'collection',
			text: 'print',
			buttons: ['csv', 'pdf']
		},
		{
			extend: 'collection',
			text: 'print',
			buttons: ['csv', 'pdf']
		}
	]
});

table.buttons( [0, 1] ).collectionRebuild(['csv', 'pdf', 'excel']);

