dt-button.example:
$('#myTable').DataTable( {
	buttons: [
		{
			extend: 'columnVisibility',
			text: 'Show all',
			visibility: true
		},
		{
			extend: 'columnVisibility',
			text: 'Hide all',
			visibility: false
		}
	]
} );

new $.fn.dataTable.Buttons( table, {
	buttons: [
		{
			extend: 'columnVisibility',
			text: 'Show secondary',
			visibility: true,
			columns: '.secondary'
		},
		{
			extend: 'columnVisibility',
			text: 'Hide secondary',
			visibility: false,
			columns: '.secondary'
		}
	]
} );
