dt-button.example:
new $.fn.dataTable.Buttons( table, {
	buttons: [
		{
            text: 'High priority',
            action: function () { ... } },
			split: [
				{ text: 'Medium priority', action: function () { ... } },
				{ text: 'Low priority',    action: function () { ... } }
			],
		}
	]
} );

