dt-option.example:
$('#myTable').DataTable( {
	buttons: {
		dom: {
			buttons: {
				tag: 'button'
			},
			buttonLiner: {
				tag: null
			}
		}
	}
} );

new $.fn.dataTable.Buttons( table, {
	buttons: {
		dom: {
			buttons: {
				tag: 'input',
				className: 'table-button'
			},
			buttonLiner: {
				tag: null
			}
		}
	]
} );
