dt-example.js:
$('#example').DataTable({
	layout: {
		topStart: {
			buttons: ['colvis']
		}
	},
	language: {
		buttons: {
			colvis: 'Change columns'
		}
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	layout: {
		topStart: {
			buttons: ['colvis']
		}
	},
	language: {
		buttons: {
			colvis: 'Change columns'
		}
	}
});
