dt-example.js:
$(document).ready(function() {
	var table = $('#example').DataTable( {
		dom: 'Bfrtip',
		buttons: ['csv', 'html', 'excel', 'pdf', 'print']
	} );

    var obj = {
        "filename":"Image",
        "blocks":[
            [
                {
                    "type":"img",
                    "children":[],
                    "href":"data:image/png;base64,"
                }
            ]
        ]
    }

	$('#dtstText').val(
		table.docGen.parse(JSON.stringify(obj, null, '\t'))
	);

});
