dt-example.js:
$(document).ready(function() {
	var table = $('#example').DataTable( {
		dom: 'Bfrtip',
		buttons: ['csv', 'html', 'excel', 'pdf', 'print']
	} );

    var obj = {
        "filename":"OneOfEvery",
        "blocks":[
            [
                {"type":"p", "children":[
                    {"type":"string", "children":[], "text":"I'm a paragraph"}
                ]},
                {"type":"p", "children":[
                    {"type":"string", "children":[], "text":"I'm a paragraph with global style", "style":"cell"}
                ]},
                {"type":"p", "children":[
                    {"type":"string", "children":[], "text":"I'm a paragraph with node specific styling", "style":{
                        "font":{"size":"20", "color":"salmon", "name":"Roboto"},
                        "border":{"style":"thin-dashed", "color":"orange"},
                        "alignment":{"horizontal":"center"},
                        "fill":{"bgColor":"lime"}
                    }}
                ]},
            ]
        ],
        "style":{
            "font":{
                "name":"Cambria"
            },
            "refs":{
                "cell":{
                    "font":{
                        "size":"45",
                        "color":"green",
                        "name":"Calibri"
                    },
                    "border":{
                        "style":"thin",
                        "color":"blue"
                    },
                    "alignment":{
                        "horizontal":"right"
                    },
                    "fill":{
                        "bgColor":"red"
                    }
                }
            }
        }
    }

	$('#dtstText').val(
		table.docGen.parse(JSON.stringify(obj))
	);

});
