dt-example.js:
$('#example').DataTable({
	layout: {
		topStart: {
			buttons: [
				{ extend: 'copyHtml5', footer: true },
				{ extend: 'excelHtml5', footer: true },
				{ extend: 'csvHtml5', footer: true },
				{ extend: 'pdfHtml5', footer: true }
			]
		}
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	layout: {
		topStart: {
			buttons: [
				{ extend: 'copyHtml5', footer: true },
				{ extend: 'excelHtml5', footer: true },
				{ extend: 'csvHtml5', footer: true },
				{ extend: 'pdfHtml5', footer: true }
			]
		}
	}
});
