dt-example.js:
$('#example').DataTable({
	layout: {
		topStart: {
			buttons: [
				{
					text: 'Popover',
					dropIcon: true,
					action: function () {
						this.popover('<span>Popover information...</span>', {
							collectionLayout: 'fixed'
						});
					}
				},
				{
					extend: 'collection',
					text: 'Export',
					buttons: ['copy', 'pdf', 'excel', 'print']
				},
				{
					extend: 'collection',
					text: 'Split',
					split: [
						{
							text: 'Inner popover',
							dropIcon: true,
							action: function () {
								this.popover('Popover information from split...');
							}
						},
						{
							extend: 'copy',
							split: ['pdf', 'excel', 'print']
						}
					]
				},
				'colvis'
			]
		}
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	layout: {
		topStart: {
			buttons: [
				{
					text: 'Popover',
					dropIcon: true,
					action: function () {
						this.popover('<span>Popover information...</span>', {
							collectionLayout: 'fixed'
						});
					}
				},
				{
					extend: 'collection',
					text: 'Export',
					buttons: ['copy', 'pdf', 'excel', 'print']
				},
				{
					extend: 'collection',
					text: 'Split',
					split: [
						{
							text: 'Inner popover',
							dropIcon: true,
							action: function () {
								this.popover('Popover information from split...');
							}
						},
						{
							extend: 'copy',
							split: ['pdf', 'excel', 'print']
						}
					]
				},
				'colvis'
			]
		}
	}
});
